<?phpif (!defined('_CAN_LOAD_FILES_'))    exit;  class Wg24Manufacturer extends Module{	function __construct()	{         $this->name = 'wg24manufacturer';        $this->author = '24WebGroup';        $this->tab = 'front_office_features';        $this->version = '1.1.0';        parent::__construct();        $this->secure_key = Tools::encrypt($this->name);        $this->displayName = $this->l('24WebGroup Home Manufacturer Module');         $this->confirmUninstall = $this->l('Are you sure you want to uninstall on this module?');        $this->description = $this->l('Home Manufacturer Module module show on home page into button section By 24WebGroup'); 		}	function install(){		if (!parent::install())			return false;		if(!$this->registerHook('displayHomeTabContent'))			return false;		if(!$this->registerHook('header'))			return false;		return true;	}	function hookdisplayHomeTabContent($params){		global $smarty, $cookie,$link;                $manufacturers = Manufacturer::getManufacturers(false,$cookie->id_lang, true);               // print_r($manufacturers); 		$smarty->assign(			array(				'manufacturers' => $manufacturers,                                'temurl'=>_PS_IMG_DIR_			)		);		return $this->display(__FILE__,'wg24manufacturer.tpl');        }}